const base64Str = $json.base64Img;

if (typeof base64Str !== 'string') {
  throw new Error("base64Img is not a string");
}

// Base64 처리
const matches = base64Str.match(/^data:(.+);base64,(.+)$/);
if (matches) {
  const mimeType = matches[1];
  const base64Data = matches[2];

  return [
    {
      binary: {
        data: {
          data: base64Data,
          mimeType,
          fileName: "image.png"
        }
      }
    }
  ];
}

// HTTP URL 처리 → throw 하되 문자열 JSON으로 포함
if (base64Str.startsWith('http')) {
  throw new Error(JSON.stringify({
    type: 'IS_HTTP_URL',
    imageUrl: base64Str
  }));
}

// 기타 예외
throw new Error("Unknown format for base64Img");

//5번째 거
function convertDate(dateStr) {
  if (!dateStr || typeof dateStr !== 'string') return null;
  const match = dateStr.match(/(\d{4})\.\s*(\d{1,2})\.\s*(\d{1,2})/);
  if (!match) return null;
  const [ , year, month, day ] = match;
  return `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
}

const geminiText = $json.text;
const jsonMatch = geminiText.match(/```json\s*([\s\S]*?)\s*```/);
if (!jsonMatch) {
  throw new Error("JSON block not found in Gemini output.");
}

// 전화번호 문자 처리
let rawJson = jsonMatch[1]
  .replace(/"inqire_co"\s*:\s*([0-9\-]+)\s*,/g, '"inqire_co": "$1",');

let parsed;
try {
  parsed = JSON.parse(rawJson);
} catch (err) {
  throw new Error("Failed to parse JSON: " + err.message);
}

const { text, ...rest } = $json;
const mergeKey = $json.fileIndex || $json.mergeKey || 0;

return {
  json: {
    ...rest,
    ...parsed,

    mergeKey: $json.mergeKey || $itemIndex,

    // ✅ flpth_nm 변환
    flpth_nm: Array.isArray(parsed.flpth_nm)
      ? parsed.flpth_nm.map(path => "www.technopark.k" + path).join("@")
      : parsed.flpth_nm ? "www.technopark.k" + parsed.flpth_nm : null,

    print_flpth_nm: Array.isArray(parsed.flpth_nm)
      ? "www.technopark.k" + parsed.flpth_nm[0]
      : parsed.flpth_nm ? "www.technopark.k" + parsed.flpth_nm : null,

    // ✅ 날짜 변환
    reqst_begin_dt: convertDate(parsed.reqst_begin_dt),
    reqst_end_dt: convertDate(parsed.reqst_end_dt),

    exc_instt_nm: parsed.exc_instt_nm,
    del_yn: "N",
    creat_p_nttm: new Date().toISOString().slice(0, 19).replace("T", " "),
    created_ts: new Date().toISOString().slice(0, 19).replace("T", " "),
    source: "한국테크노파크"
  }
};

const { data, mergeKey,biz_info_id, base64Img, ...rest } = $json;

// ✅ flpth_nm 변환
if (Array.isArray(rest.flpth_nm)) {
  rest.print_flpth_nm = "http://www.technopark.kr/" + rest.flpth_nm[0];
  rest.flpth_nm = rest.flpth_nm.map(path => "http://www.technopark.kr/" + path).join("@");
} else if (typeof rest.flpth_nm === "string") {
  rest.print_flpth_nm = "http://www.technopark.kr/" + rest.flpth_nm;
  rest.flpth_nm = "http://www.technopark.kr/" + rest.flpth_nm;
} else {
  rest.print_flpth_nm = null;
  rest.flpth_nm = null;
}

// ✅ file_nm: 배열이면 문자열로 결합 (@ 구분)
if (Array.isArray(rest.file_nm)) {
  rest.file_nm = rest.file_nm.join("@");
}

// ✅ print_file_nm 설정 (첫 번째 파일 이름)
rest.print_file_nm = rest.file_nm?.split("@")[0] || null;

// ✅ 주요 필드 이스케이프 처리
function escapeSQL(value) {
  return typeof value === 'string' ? value.replace(/'/g, "''") : value;
}
const fieldsToEscape = [
  "pblanc_nm", "bsns_sumry_cn", "support_target",
  "bonus_points_factors", "subsidy_amount_type",
  "total_budget_type", "print_file_nm", "file_nm"
];
for (const field of fieldsToEscape) {
  if (rest[field] !== undefined) {
    rest[field] = escapeSQL(rest[field]);
  }
}

return { json: rest };

// 원래 에러 메시지에서 URL만 추출
const errorMessage = $json.error;

// 정규식으로 //로 시작해서 .bmp 로 끝나는 URL 부분만 추출
const match = errorMessage.match(/(\/\/[^\s"]+\.bmp)/);

if (!match) {
  throw new Error("No valid image URL found in error message");
}

let url = match[1]; // ex: //www.gwtp.or.kr/...

// https 붙이기
url = "https:" + url;

// 에러 객체로 다시 throw (이건 분기 처리용)
throw new Error(JSON.stringify({
  type: "IS_HTTP_URL",
  imageUrl: url
}));
