function convertDate(dateStr) {
  if (!dateStr || typeof dateStr !== 'string') return null;
  const match = dateStr.match(/(\d{4})\.\s*(\d{1,2})\.\s*(\d{1,2})/);
  if (!match) return null;
  const [ , year, month, day ] = match;
  return `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
}

const geminiText = $json.text;
const jsonMatch = geminiText.match(/```json\s*([\s\S]*?)\s*```/);
if (!jsonMatch) {
  throw new Error("JSON block not found in Gemini output.");
}

// 전화번호 문자 처리
let rawJson = jsonMatch[1]
  .replace(/"inqire_co"\s*:\s*([0-9\-]+)\s*,/g, '"inqire_co": "$1",');

let parsed;
try {
  parsed = JSON.parse(rawJson);
} catch (err) {
  throw new Error("Failed to parse JSON: " + err.message);
}

const { text, ...rest } = $json;
const mergeKey = $json.fileIndex || $json.mergeKey || 0;

return {
  json: {
    ...rest,
    ...parsed,
    mergeKey,

    // ✅ flpth_nm 변환
    flpth_nm: Array.isArray(parsed.flpth_nm)
      ? parsed.flpth_nm.map(path => "https://www.kicox.or.kr" + path).join("@")
      : parsed.flpth_nm ? "https://www.kicox.or.kr" + parsed.flpth_nm : null,

    print_flpth_nm: Array.isArray(parsed.flpth_nm)
      ? "https://www.kicox.or.kr" + parsed.flpth_nm[0]
      : parsed.flpth_nm ? "https://www.kicox.or.kr" + parsed.flpth_nm : null,

    // ✅ 날짜 변환
    reqst_begin_dt: convertDate(parsed.reqst_begin_dt),
    reqst_end_dt: convertDate(parsed.reqst_end_dt),

    exc_instt_nm: parsed.exc_instt_nm,
    del_yn: "N",
    creat_p_nttm: new Date().toISOString().slice(0, 19).replace("T", " "),
    created_ts: new Date().toISOString().slice(0, 19).replace("T", " "),
    source: "한국산업단지공단"
  }
};

