const baseUrl = "https://www.kicox.or.kr";

// flpth_nm 처리: URL 붙이고 @로 연결
let updatedFlpthNm = null;
let firstPath = null;

if (Array.isArray($json.flpth_nm)) {
  updatedFlpthNm = $json.flpth_nm.map(path => `${baseUrl}${path.trim()}`).join("@");
  firstPath = `${baseUrl}${$json.flpth_nm[0].trim()}`;
} else if (typeof $json.flpth_nm === "string") {
  const paths = $json.flpth_nm.split("@").map(p => p.trim());
  updatedFlpthNm = paths.map(path => `${baseUrl}${path}`).join("@");
  if (paths.length > 0) {
    firstPath = `${baseUrl}${paths[0]}`;
  }
}

// file_nm 처리: 배열이면 @로 연결, 아니면 그대로
let updatedFileNm = null;
if (Array.isArray($json.file_nm)) {
  updatedFileNm = $json.file_nm.map(name => name.trim()).join("@");
} else if (typeof $json.file_nm === "string") {
  updatedFileNm = $json.file_nm.trim();
}

