  "total_budget_type", "equipment_detail_file_path", "equipment_detail_file_name"
];

// 필드 정리
const filtered = {};
for (const key of allowedFields) {
  if (key === "flpth_nm") {
    filtered[key] = updatedFlpthNm;
  } else if (key === "print_flpth_nm") {
    filtered[key] = firstPath || null;
