const announcements = items[0]?.json?.announcements || [];
const titles = items[0]?.json?.title || [];
const dates = items[0]?.json?.date || [];

const backCd = items[0]?.json?.backCd || "Y";
const viewPblancNm = items[0]?.json?.viewPblancNm || "";
const tabNameArray = items[0]?.json?.tabName || [];
const curPage = items[0]?.json?.curPage || "1";

// 어제 날짜 (yyyy-mm-dd)
const yesterday = new Date();
yesterday.setDate(yesterday.getDate() - 1);
const yesterdayStr = yesterday.toISOString().slice(0, 10);

// 공고 URL 생성 함수
function parseFnIncludeParams(jsString) {
  const match = jsString.match(/fn_include_popOpen2\(([^)]+)\)/);
  if (!match) return {};

  const params = match[1].split(',').map(p => p.trim().replace(/^'|'$/g, ''));

  const viewInsttNmEncoded = params[4] && params[4] !== '' ? encodeURIComponent(params[4]) : encodeURIComponent('정보없음');
  const pblancNowStatEncoded = params[5] ? encodeURIComponent(params[5].replace(/\s+/g, '+')) : null;

  return {
    viewPblancSeq: params[0] || null,
    viewPage: "",
    viewCntcInsttCd: params[2] || null,
    viewPblancId: params[3] || null,
    viewInsttNm: viewInsttNmEncoded,
    insttName: params[4] || null,
    pblancNowStat: pblancNowStatEncoded,
    status: params[5] || null,
    // -------------------- 다운로드 링크 생성 --------------------
    downloadLink: params[0] ? `https://www.smes.go.kr/main/comm/getFile?srvcId=CNTCWRTANC&upperNo=${params[0]}&fileTy=CNTCATTACH&fileNo=1` : null
  };
}

// 매핑 및 필터링
return announcements.map(function(announcement, index) {
  const rawDate = dates[index] || "";
  const startDate = rawDate.trim().split("~")[0]?.trim();

  const parsed = parseFnIncludeParams(announcement);

  return {
    json: {
      announcement,
      title: titles[index] || null,
      date: startDate,
      ...parsed,
      viewPblancNm: encodeURIComponent(viewPblancNm || ""),
      backCd: backCd || "Y",
      tabName: tabNameArray.length ? tabNameArray : ['공고검색'],
      curPage: curPage || "1",
      cookie:$('HTTP Request3').first().json.headers['set-cookie'][0]
    }
  };
}).filter(item => item.json.date === yesterdayStr);

const input = $input.first().json;

// 현재 KST 시간 생성
const now = new Date();
const kst = new Date(now.getTime() + 9 * 60 * 60 * 1000);

const yyyy = kst.getFullYear();
const mm = String(kst.getMonth() + 1).padStart(2, '0');
const dd = String(kst.getDate()).padStart(2, '0');
const hh = String(kst.getHours()).padStart(2, '0');
const min = String(kst.getMinutes()).padStart(2, '0');
const ss = String(kst.getSeconds()).padStart(2, '0');

const formattedKST = `${yyyy}-${mm}-${dd} ${hh}:${min}:${ss}`;

// 날짜 포맷 함수
function formatYYYYMMDD(dateStr) {
  return dateStr.replace(/-/g, '');
}

// 지원 대상 텍스트 정리
const supportTarget = input.support_target?.replace(/\\n/g, '').trim() ?? '';

// 신청 기간 가공
let reqstBegin = '';
let reqstEnd = '';
let reqstBeginEndDeFormatted = '';
if (input.reqst_begin_end_de) {
  const match = input.reqst_begin_end_de.match(/(\d{4}-\d{2}-\d{2})\s*~\s*(\d{4}-\d{2}-\d{2})/);
  if (match) {
    reqstBegin = match[1];
    reqstEnd = match[2];
    reqstBeginEndDeFormatted = `${formatYYYYMMDD(reqstBegin)} ~ ${formatYYYYMMDD(reqstEnd)}`;
  }
}

// 첨부파일 URL 생성
const fileBaseUrl = 'http://www.smes.go.kr';
const fullFileUrl = fileBaseUrl + (input.print_flpth_nm || '');

// 확장자 추출
const extension = input.print_file_nm?.split('.').pop() ?? '';

return {
  json: {
    title: input.title,
    exc_instt_nm: input.exc_instt_nm,
    bsns_sumry_cn: input.bsns_sumry_cn,
    support_target: supportTarget,
    reqst_begin_end_de: reqstBeginEndDeFormatted, // yyyymmdd ~ yyyymmdd
    reqst_begin_dt: reqstBegin, // yyyy-mm-dd
    reqst_end_dt: reqstEnd,     // yyyy-mm-dd
    reqst_mth_papers_cn: input.reqst_mth_papers_cn,
    refrnc_nm: input.refrnc_nm,
    print_flpth_nm: input.print_flpth_nm,
    print_file_nm: input.print_file_nm,
    print_file_url: fullFileUrl,
    print_file_ext: extension,
  }
};

const pages = $input.first().json.pages;
let combinedMarkdown = '';

for (const page of pages) {
  combinedMarkdown += page.markdown + '\n';  // 줄바꿈 포함해서 합치기
}

// 생성일자: KST 기준 현재 시각
const now = new Date();
const kst = new Date(now.getTime() + 9 * 60 * 60 * 1000);

const yyyy = kst.getFullYear();
const mm = String(kst.getMonth() + 1).padStart(2, '0');
const dd = String(kst.getDate()).padStart(2, '0');
const hh = String(kst.getHours()).padStart(2, '0');
const min = String(kst.getMinutes()).padStart(2, '0');
const ss = String(kst.getSeconds()).padStart(2, '0');

const formatted = `${yyyy}-${mm}-${dd} ${hh}:${min}:${ss}`;

// 통합 결과 반환
return [
  {
    json: {
      combinedMarkdown,
      creat_p_nttm: formatted,
      created_ts: formatted
    }
  }
];

// Loop over input items
for (const item of $input.all()) {
  // 괄호 제거
  if (item.json.pblanc_nm) {
    item.json.pblanc_nm = item.json.pblanc_nm.replace(/[\(\[][^()\[\]]+[\)\]]/g, '').trim();
  }

  // 새 필드 추가
  item.json.myNewField = 1;
}

return $input.all();

// Loop over input items
for (const item of $input.all()) {
  // 괄호 제거
  if (item.json.title) {
    item.json.title = item.json.title.replace(/[\(\[][^()\[\]]+[\)\]]/g, '').trim();
  }

  // 새 필드 추가
  item.json.myNewField = 1;
}

return $input.all();

const setCookieHeader = $input.first().json.headers['set-cookie'][0];

// 'JSESSIONID=...' 부분만 추출 (세미콜론 기준으로 첫 번째 항목)
const jsessionId = setCookieHeader.split(';')[0];

return {
  cookie: jsessionId
};

// Function 노드 안
return [
  {
    json: {
      workflowName: $workflow.name,
      insertId:$input.first().json.data.insertId
    }
  }
];
