const items = $input.all();

const announcements = $input.first().json.announcements
const titles = $input.first().json.titles
const dates = $input.first().json.dates

// -------------------- 어제 날짜 계산 --------------------
const yesterday = new Date();
yesterday.setDate(yesterday.getDate() - 1);
const yyyy = yesterday.getFullYear();
const mm = String(yesterday.getMonth() + 1).padStart(2, '0');
const dd = String(yesterday.getDate()).padStart(2, '0');
// 자동 어제 날짜
let yesterdayStr = `${yyyy}-${mm}-${dd}`; 

// -------------------- 테스트용 날짜 지정 --------------------
// 필요할 때만 아래 주석 해제하여 테스트
// let yesterdayStr = '2024-12-23'; 
// ------------------------------------------------------

// 배열 매핑 후 어제 날짜만 필터
const result = announcements
  .map((announcement, index) => {
    // announcement에서 uni_ancm_id 추출
    const match = announcement.match(/uni_ancm_id=([^&]+)/);
    const extracted = match ? match[1].trim() : announcement;

    return {
      json: {
        announcement: extracted,
        title: (titles[index] || '').replace(/[\[\]]/g, '').trim(),
        date: dates[index] || null
      }
    };
  })
  .filter(item => item.json.date === yesterdayStr); // 날짜만 조건

return result;

// Loop over input items
for (const item of $input.all()) {
  // 괄호 제거
  if (item.json.pblanc_nm) {
    item.json.pblanc_nm = item.json.pblanc_nm.replace(/[\(\[][^()\[\]]+[\)\]]/g, '').trim();
  }

  // 새 필드 추가
  item.json.myNewField = 1;
}

return $input.all();

let title = $input.first().json.title || '';
let announcement = $input.first().json.announcement

// 괄호 또는 대괄호 안의 내용 제거
title = title.replace(/[\(\[][^()\[\]]+[\)\]]/g, '').trim();

return [
  {
    json: {
      title,
      announcement
    }
  }
];

// n8n Function 노드 예시
// $input 사용 안하고 바로 값 반환

// 1. 현재 년도 구하기
let currentYear = new Date().getFullYear();

// 2. 임의 값으로 바꾸고 싶으면 여기서 설정 가능
// currentYear = 2024; 

return [
  {
    json: {
      year: currentYear
    }
  }
];

// 입력 데이터에서 list 배열 꺼내기
const list = $input.first().json.data.list

// ancmId, ancmTl, ancmDe 각각 배열로 추출
const announcements = list.map(item => item.ancmId);
const titles = list.map(item => item.ancmTl);
const dates = list.map(item => item.ancmDe);

// 결과 반환
return [
  {
    json: {
      announcements, // ancmId 배열
      titles,        // 제목 배열
      dates          // 날짜 배열
    }
  }
];

const rawPages = $input.first().json.pages || "";

// 1. BOM, 탭, 줄바꿈 정리
let cleaned = rawPages
  .replace(/\uFEFF/g, '')    // BOM 제거
  .replace(/\t+/g, ' ')      // 탭 → 공백
  .replace(/\s*\n\s*/g, '\n') // 양쪽 공백 포함한 줄바꿈 정리
  .replace(/\n{2,}/g, '\n')  // 연속 줄바꿈 → 하나로
  .replace(/ {2,}/g, ' ')    // 다중 공백 → 하나로
  .trim();

// 생성일자: KST 기준 현재 시각
const now = new Date();
const kst = new Date(now.getTime() + 9 * 60 * 60 * 1000);

const yyyy = kst.getFullYear();
const mm = String(kst.getMonth() + 1).padStart(2, '0');
const dd = String(kst.getDate()).padStart(2, '0');
const hh = String(kst.getHours()).padStart(2, '0');
const min = String(kst.getMinutes()).padStart(2, '0');
const ss = String(kst.getSeconds()).padStart(2, '0');

const formatted = `${yyyy}-${mm}-${dd} ${hh}:${min}:${ss}`;

return [
  {
    json: {
      pages: cleaned,    // 정리된 텍스트만 반환
      creat_p_nttm: formatted,
      created_ts: formatted
    }
  }
];
