const items = $input.all();

const announcements = items[0]?.json?.announcements || [];
const titles = items[0]?.json?.title || [];
const dates = items[0]?.json?.date || [];

// -------------------- 어제 날짜 계산 --------------------
const yesterday = new Date();
yesterday.setDate(yesterday.getDate() - 1);
const yyyy = yesterday.getFullYear();
const mm = String(yesterday.getMonth() + 1).padStart(2, '0');
const dd = String(yesterday.getDate()).padStart(2, '0');
const yesterdayStr = `${yyyy}-${mm}-${dd}`;   // 자동 계산된 어제 날짜
// ------------------------------------------------------

// -------------------- 테스트용 날짜 지정 --------------------
// const yesterdayStr = '2025-09-08'; // 수동으로 테스트 시 이 주석 해제
// ------------------------------------------------------

// -------------------- 결과 생성 --------------------
const result = announcements
  .map((announcement, index) => {
    const url = announcement?.trim() || null;
    let dateStr = dates[index] || null;

    // "2025-09-08 00:00 ~ 2025-09-15 23:50" → 앞부분 날짜만 추출
    if (dateStr) {
      const match = dateStr.match(/^(\d{4}-\d{2}-\d{2})/);
      if (match) dateStr = match[1];
    }

    return {
      json: {
        announcement: url,
        title: (titles[index] || '').replace(/[\[\]]/g, '').trim(),
        date: dateStr
      }
    };
  })
  // -------------------- 어제 날짜만 필터 --------------------
  .filter(item => item.json.date === yesterdayStr);

return result;

// Loop over input items
for (const item of $input.all()) {
  // 괄호 제거
  if (item.json.pblanc_nm) {
    item.json.pblanc_nm = item.json.pblanc_nm.replace(/[\(\[][^()\[\]]+[\)\]]/g, '').trim();
  }

  // 새 필드 추가
  item.json.myNewField = 1;
}

return $input.all();

let title = $input.first().json.title || '';
let announcement = $input.first().json.announcement

// 괄호 또는 대괄호 안의 내용 제거
title = title.replace(/[\(\[][^()\[\]]+[\)\]]/g, '').trim();

return [
  {
    json: {
      title,
      announcement
    }
  }
];

const pages = $input.first().json.pages;
let combinedMarkdown = '';

for (const page of pages) {
  combinedMarkdown += page.markdown + '\n';  // 줄바꿈 포함해서 합치기
}

// 생성일자: KST 기준 현재 시각
const now = new Date();
const kst = new Date(now.getTime() + 9 * 60 * 60 * 1000);

const yyyy = kst.getFullYear();
const mm = String(kst.getMonth() + 1).padStart(2, '0');
const dd = String(kst.getDate()).padStart(2, '0');
const hh = String(kst.getHours()).padStart(2, '0');
const min = String(kst.getMinutes()).padStart(2, '0');
const ss = String(kst.getSeconds()).padStart(2, '0');

const formatted = `${yyyy}-${mm}-${dd} ${hh}:${min}:${ss}`;

// 통합 결과 반환
return [
  {
    json: {
      combinedMarkdown,
      creat_p_nttm: formatted,
      created_ts: formatted
    }
  }
];

const items = $('리스트 추출4').first().json;
const names = items.attach_file_name || [];
const addresses = items.attach_file_address || [];

let noticeFileName = null;
let noticeFileAddress = null;
const extraFileNames = [];
const extraFilePaths = [];

for (let i = 0; i < names.length; i++) {
    let name = names[i]
        ?.replace(/[\n\r]/g, "")
        .replace(/\([\d\.]+[KMG]?B\)/i, "")
        .trim();

    let address = addresses[i];
    if (!name || !address) continue;

    // fn_download('ID', 'NUM', 'EXT') → ID만 추출
    const match = address.match(/fn_download\(\s*'([^']+)'\s*,\s*'[^']*'\s*,\s*'[^']*'\s*\)/);
    if (!match) continue;
    const fileUrl = match[1];

    // "공고" 포함 파일은 noticeFile로
    if (name.includes("공고") && !noticeFileName) {
        noticeFileName = name;
        noticeFileAddress = fileUrl;
    } else {
        extraFileNames.push(name);
        extraFilePaths.push(fileUrl);
    }
}

// 공고 파일이 없으면 첫 번째 파일로 대체
if (!noticeFileName && names.length > 0 && addresses.length > 0) {
    noticeFileName = names[0]
        ?.replace(/[\n\r]/g, "")
        .replace(/\([\d\.]+[KMG]?B\)/i, "")
        .trim();

    const firstMatch = addresses[0].match(/fn_download\(\s*'([^']+)'\s*,\s*'[^']*'\s*,\s*'[^']*'\s*\)/);
    noticeFileAddress = firstMatch ? firstMatch[1] : null;

    // 첫 번째 파일가 이미 extraFileNames에 들어간 경우 제거
    const index = extraFileNames.indexOf(noticeFileName);
    if (index > -1) {
        extraFileNames.splice(index, 1);
        extraFilePaths.splice(index, 1);
    }
}

return [{
    json: {
        fileName: noticeFileName,
        fileAddress: noticeFileAddress,
        file_nm: extraFileNames.join("@"),
        flpth_nm: extraFilePaths.join("@")
    }
}];

// HTML 전체 문자열
const htmlData = $('board list').first().json.data;

// 1️⃣ 제목 추출 (unescape 안 문자열)
const titleRegex = /unescape\('([^']+)'\)/g;
let match;
const titles = [];
while ((match = titleRegex.exec(htmlData)) !== null) {
  // HTML 엔티티 디코딩 (예: &#034; -> ")
  const decodedTitle = match[1].replace(/&#034;/g, '"').replace(/&amp;/g, '&').replace(/&lt;/g, '<').replace(/&gt;/g, '>');
  titles.push(decodedTitle);
}

// 2️⃣ 날짜(REG_DT) 추출
const dateRegex = /\$\('#td_REG_DT_\d+'\)\.html\('([^']+)'\)/g;
const dates = [];
while ((match = dateRegex.exec(htmlData)) !== null) {
  dates.push(match[1]);
}

// 3️⃣ fn_detail() 값 추출
const fnDetailRegex = /fn_detail\((\d+)\)/g;
const fnDetails = [];
while ((match = fnDetailRegex.exec(htmlData)) !== null) {
  fnDetails.push(match[1]);
}

// 4️⃣ 제목, 날짜, fn_detail 매칭
const announcements = titles.map((title, idx) => {
  return {
    json: {
      title: title,
      date: dates[idx] || null,
      fn_detail: fnDetails[idx] || null
    }
  };
});

return announcements;

// ----------------------
// 🔹 테스트용 데이터 예시
// ----------------------
// 오늘이 2025년 9월 11일이라고 가정할 때
// "2025. 9. 10" → 어제 → isYesterday: true
// "2025. 9. 9"  → 그저께 → isYesterday: false
// "2025. 9. 11" → 오늘 → isYesterday: false
// const items = [
//   { json: { date: "2025. 9. 10" } },
//   { json: { date: "2025. 9. 9" } },
//   { json: { date: "2025. 9. 11" } },
// ];

// ----------------------
// 🔹 함수 실행
// ----------------------
return items.map(item => {
  const inputDateStr = item.json.date; // "YYYY. M. D" 형식

  // 문자열을 Date 객체로 변환 (한국 시간 기준)
  const [year, month, day] = inputDateStr.split('.').map(s => parseInt(s.trim()));
  const inputDate = new Date(Date.UTC(year, month - 1, day, 0, 0, 0)); // UTC 기준
  inputDate.setHours(inputDate.getHours() + 9); // 한국 시간으로 조정

  // 오늘 한국 시간
  const now = new Date();
  const kstNow = new Date(now.getTime() + 9 * 60 * 60 * 1000);

  // 어제 한국 시간
  const yesterday = new Date(kstNow);
  yesterday.setDate(yesterday.getDate() - 1);
  yesterday.setHours(0, 0, 0, 0);

  // 입력 날짜가 어제인지 확인
  const isYesterday = inputDate.getFullYear() === yesterday.getFullYear() &&
                      inputDate.getMonth() === yesterday.getMonth() &&
                      inputDate.getDate() === yesterday.getDate();

  return {
    json: {
      ...item.json,
      isYesterday // true/false로 결과 확인 가능
    }
  };
});

// Function 노드 안
return [
  {
    json: {
      workflowName: $workflow.name,
      insertId:$input.first().json.data.insertId
    }
  }
];
