const items = $input.all();

const announcements = items[0]?.json?.announcements || [];
const titles = items[0]?.json?.title || [];

// -------------------- 어제 날짜 계산 --------------------
const yesterday = new Date();
yesterday.setDate(yesterday.getDate() - 1);
const yyyy = yesterday.getFullYear();
const mm = String(yesterday.getMonth() + 1).padStart(2, '0');
const dd = String(yesterday.getDate()).padStart(2, '0');
let yesterdayStr = `${yyyy}-${mm}-${dd}`;   // YYYY-MM-DD 형식
// ------------------------------------------------------

// -------------------- 테스트용 날짜 지정 --------------------
// 테스트용으로 특정 날짜 확인 시 아래 주석 해제 후 사용
// yesterdayStr = '2025-09-05';  // YYYY-MM-DD 형식으로 원하는 날짜 입력
// ------------------------------------------------------

// -------------------- 결과 생성 --------------------
const result = announcements
  .map((announcement, index) => {
    const link = announcement;
    const rawTitle = titles[index] || '';

    // 정규식으로 날짜 추출 (예: 2025.09.05)
    const match = rawTitle.match(/(\d{4})\.(\d{2})\.(\d{2})/);
    let dateStr = null;
    if (match) {
      dateStr = `${match[1]}-${match[2]}-${match[3]}`;
    }

    // roRndUid 값 추출
    const uidMatch = link.match(/roRndUid=(\d+)/);
    const uid = uidMatch ? uidMatch[1] : null;

    return {
      json: {
        announcement: link,                                // 공고 링크
        title: rawTitle.replace(/[\[\]]/g, '').trim(),     // 제목
        date: dateStr,                                     // 추출한 날짜
        uid: uid                                           // roRndUid 값
      }
    };
  })
  // 어제 날짜만 필터링
  .filter(item => item.json.date === yesterdayStr);

return result;

let title = $input.first().json.title || '';
let announcement = $input.first().json.announcement
let program = $input.first().json.program

// 괄호 또는 대괄호 안의 내용 제거
title = title.replace(/[\(\[][^()\[\]]+[\)\]]/g, '').trim();

return [
  {
    json: {
      title,
      announcement,
      program,
    }
  }
];

const items = $('리스트 추출2').first().json;
const names = items.attach_file_name || [];
const addresses = items.attach_file_address || [];
const baseUrl = "https://www.ntis.go.kr";

let noticeFileName = null;
let noticeFileAddress = null;
let announcement_file_exist = false;

const extraFileNames = [];
const extraFilePaths = [];

// -------------------- KST 기준 현재 시각 --------------------
const now = new Date();
const kst = new Date(now.getTime() + 9 * 60 * 60 * 1000);

const yyyy = kst.getFullYear();
const mm = String(kst.getMonth() + 1).padStart(2, '0');
const dd = String(kst.getDate()).padStart(2, '0');
const hh = String(kst.getHours()).padStart(2, '0');
const min = String(kst.getMinutes()).padStart(2, '0');
const ss = String(kst.getSeconds()).padStart(2, '0');

const formatted = `${yyyy}-${mm}-${dd} ${hh}:${min}:${ss}`;

// -------------------- 파일명 정리 --------------------
function normalizeFileName(name) {
    return name
        ?.replace(/[\n\r\t]/g, "")                           // 줄바꿈, 탭 제거
        .replace(/\(파일크기:\s*[\d\.]+[KMG]?B\s*\)/gi, "")  // (파일크기: 2 MB) 제거
        .trim();
}

function normalizeFileUrl(address) {
    if (!address) return null;
    if (address.startsWith("http")) return address;
    return `${baseUrl}${address}`;
}

// -------------------- 파일 분리 --------------------
for (let i = 0; i < names.length; i++) {
    const name = normalizeFileName(names[i]);
    const fileUrl = normalizeFileUrl(addresses[i]);
    if (!name || !fileUrl) continue;

    if (!noticeFileName) {
        // 첫 번째 파일은 공고 파일로
        noticeFileName = name;
        noticeFileAddress = fileUrl;
        announcement_file_exist = true;
    } else {
        extraFileNames.push(name);
        extraFilePaths.push(fileUrl);
    }
}

// -------------------- 날짜 변환 함수 --------------------
function formatDate(raw) {
    if (!raw) return null;
    const match = raw.match(/(\d{4})\.(\d{2})\.(\d{2})/);
    return match ? `${match[1]}-${match[2]}-${match[3]}` : null;
}

const startDate = formatDate(items.start_date);
const endDate = formatDate(items.end_date);
let startEndDate = null;
if (startDate && endDate) {
    startEndDate = `${startDate.replace(/-/g, '')} ~ ${endDate.replace(/-/g, '')}`;
}

// -------------------- 결과 반환 --------------------
return [{
    json: {
        fileName: noticeFileName,              // 공고 파일명 (첫 번째 파일)
        fileAddress: noticeFileAddress,        // 공고 파일 URL
        file_nm: extraFileNames.join("@"),     // 기타 파일명
        flpth_nm: extraFilePaths.join("@"),    // 기타 파일 URL
        announcement_file_exist: announcement_file_exist,
        content: items.content,                // 본문 내용
        start_date: startDate,                 // YYYY-MM-DD
        end_date: endDate,                     // YYYY-MM-DD
        start_end_date: startEndDate,          // YYYYMMDD ~ YYYYMMDD
        creat_p_nttm: formatted,
        created_ts: formatted
    }
}];
