const items = $input.all();

const announcements = items[0]?.json?.announcements || [];
const titles = items[0]?.json?.title || [];
const dates = items[0]?.json?.date || [];

// -------------------- 어제 날짜 계산 --------------------
const yesterday = new Date();
yesterday.setDate(yesterday.getDate() - 1);

const yyyy = yesterday.getFullYear();
const mm = String(yesterday.getMonth() + 1).padStart(2, '0');
const dd = String(yesterday.getDate()).padStart(2, '0');

let yesterdayStr = `${yyyy}-${mm}-${dd}`; // 자동 계산된 어제 날짜
// ------------------------------------------------------

// -------------------- 테스트용 날짜 지정 --------------------
// 수동 테스트 시 아래 주석 해제
// yesterdayStr = '2025-06-30';
// ------------------------------------------------------

// -------------------- baseurl 설정 --------------------
const baseUrl = "https://www.kobc.or.kr";
// ------------------------------------------------------

// -------------------- 결과 생성 --------------------
const result = announcements
  .map((announcement, index) => {
    let url = announcement?.trim() || null;

    // goTo.view 패턴 처리
    let match = url?.match(/goTo\.view\('view','(\d+)','(\d+)','(\d+)'\)/);
    if (match) {
      const bIdx = match[1];
      const ptIdx = match[2];
      const mId = match[3];
      url = `${baseUrl}/ebz/kor/bbs/view.do?mId=${mId}&bIdx=${bIdx}&ptIdx=${ptIdx}`;
    }

    // 날짜 정리
    let dateStr = dates[index] || null;
    if (dateStr) {
      dateStr = dateStr.replace(/\s+/g, '').replace(/\.$/, '');
      const dateMatch = dateStr.match(/(\d{4})[.\-](\d{1,2})[.\-](\d{1,2})/);
      if (dateMatch) {
        const y = dateMatch[1];
        const m = dateMatch[2].padStart(2, '0');
        const d = dateMatch[3].padStart(2, '0');
        dateStr = `${y}-${m}-${d}`;
      }
    }

    return {
      json: {
        announcement: url,
        title: (titles[index] || '').replace(/[\[\]]/g, '').trim(),
        date: dateStr
      }
    };
  })
  // 어제 날짜와 일치하는 항목만 필터
  .filter(item => item.json.date === yesterdayStr);

return result;

let title = $input.first().json.title || '';
let announcement = $input.first().json.announcement

// 괄호 또는 대괄호 안의 내용 제거
title = title.replace(/[\(\[][^()\[\]]+[\)\]]/g, '').trim();

return [
  {
    json: {
      title,
      announcement
    }
  }
];

