const items = $('리스트 추출2').first().json;
const names = items.attach_file_name || [];
const addresses = items.attach_file_address || [];

// -------------------- baseurl (사이트마다 맞게 변경) --------------------
const baseUrl = "https://9988.kospo.co.kr";

let noticeFileName = null;
let noticeFileAddress = null;
let announcement_file_exist = false;

const extraFileNames = [];
const extraFilePaths = [];

// -------------------- 현재 시각 (KST) --------------------
const now = new Date();
const kst = new Date(now.getTime() + 9 * 60 * 60 * 1000);
const yyyy = kst.getFullYear();
const mm = String(kst.getMonth() + 1).padStart(2, '0');
const dd = String(kst.getDate()).padStart(2, '0');
const hh = String(kst.getHours()).padStart(2, '0');
const min = String(kst.getMinutes()).padStart(2, '0');
const ss = String(kst.getSeconds()).padStart(2, '0');
const formatted = `${yyyy}-${mm}-${dd} ${hh}:${min}:${ss}`;

// -------------------- 파일명 전처리 --------------------
function normalizeFileName(name) {
    return name
        ?.replace(/[\n\r\t]/g, "")
        .replace(/\(파일크기:\s*[\d\.]+[KMG]?B\s*\)$/i, "")
        .trim();
}

// -------------------- 주소 → 다운로드 URL 변환 --------------------
function extractFileUrl(address) {
    if (!address) return null;

    // 1. javascript:download('32415') 패턴 처리
    let match = address.match(/download\('(\d+)'\)/);
    if (match) {
        const attachNo = match[1];
        return `${baseUrl}/_custom/cms/_common/board/fira.jsp?attach_no=${attachNo}`;
    }

    // 2. fn_egov_downFile('atchFileId','fileSn') 패턴
    match = address.match(/fn_egov_downFile\('([^']+)','([^']+)'/);
    if (match) {
        const atchFileId = match[1];
        const fileSn = match[2];
        return `${baseUrl}/cmm/fms/FileDown.do?atchFileId=${encodeURIComponent(atchFileId)}&fileSn=${encodeURIComponent(fileSn)}`;
    }

    // 3. yhLib.file.download 패턴
    match = address.match(/yhLib\.file\.download\('([^']+)'/);
    if (match) return `${baseUrl}/download.action?attachId=${match[1]}`;

    // 4. 상대경로나 절대경로 처리
    if (address.startsWith("./")) return baseUrl + address.substring(1);
    if (address.startsWith("http")) return address;

    return `${baseUrl}${address}`;
}

// -------------------- 파일 분류 --------------------
for (let i = 0; i < names.length; i++) {
    const name = normalizeFileName(names[i]);
    const fileUrl = extractFileUrl(addresses[i]);
    if (!name || !fileUrl) continue;

    // zip 파일은 extra로 이동
    if (name.toLowerCase().endsWith(".zip")) {
        extraFileNames.push(name);
        extraFilePaths.push(fileUrl);
        continue;
    }

    // 공고문 파일 우선 선택
    if (name.includes("공고") && !noticeFileName) {
        noticeFileName = name;
        noticeFileAddress = fileUrl;
        announcement_file_exist = true;
    } else {
        extraFileNames.push(name);
        extraFilePaths.push(fileUrl);
    }
}

// -------------------- 공고 파일 없는 경우 첫 번째 파일 대체 --------------------
if (!noticeFileName && names.length > 0) {
    for (let i = 0; i < names.length; i++) {
        const candidateName = normalizeFileName(names[i]);
        const candidateUrl = extractFileUrl(addresses[i]);
        if (!candidateName.toLowerCase().endsWith(".zip")) {
            noticeFileName = candidateName;
            noticeFileAddress = candidateUrl;
            break;
        }
    }
}

// -------------------- 최종 리턴 --------------------
return [{
    json: {
        fileName: noticeFileName,
        fileAddress: noticeFileAddress,
        file_nm: extraFileNames.join("@"),
        flpth_nm: extraFilePaths.join("@"),
        announcement_file_exist: announcement_file_exist,
        creat_p_nttm: formatted,
        created_ts: formatted
    }
}];

// Function 노드 안
return [
  {
    json: {
      workflowName: $workflow.name,
      insertId:$input.first().json.data.insertId
    }
  }
];

