const html = $json.body || $json.data || '';

const regex = /href="(\/user\/bbs\/BD_selectBbs\.do[^"]+)[^>]*?">\s*([\s\S]*?)<\/a>/g;
const matches = html.matchAll(regex);

const results = [];
for (const match of matches) {
  const rawUrl = match[1];
  const rawTitle = match[2];

  // HTML 엔티티 디코딩 (예: &amp; -> &)
  const decodedUrl = rawUrl.replace(/&amp;/g, '&');

  // 태그 제거 + 공백 정리
  const cleanTitle = rawTitle.replace(/<[^>]+>/g, '').replace(/\s+/g, ' ').trim();

  results.push({
    url: "https://www.kicox.or.kr" + decodedUrl,
    title: cleanTitle,
  });
}

return results.map(r => ({ json: r }));

const results = [];
const fileRe = /href="(\/component\/file\/ND_fileDownload\.do\?[^"]+)"[^>]*>([^<]+)<\/a>/gi;

for (const item of items) {
  const html = item.json.body || item.json.data || '';
  for (const m of html.matchAll(fileRe)) {
    const dl = 'https://www.kicox.or.kr' + m[1].replace(/&amp;/g,'&');
    results.push({json:{
      sourceUrl: item.json.url,
      fileName : m[2].trim(),
      downloadUrl: decodeURIComponent(dl)
    }});
  }
}
return results;

