return items.map(item => {
  const raw = item.json.downloadUrl || '';
  const cleaned = raw
    .toString()
    .replace(/[\n\r\t\s]+/g, '')         // 공백 및 줄바꿈 제거
    .replace(/&amp;/g, '&')              // HTML 인코딩 된 & 해제
    .trim();
