// Gemini 또는 LLM 응답의 텍스트 내용만 추출
const geminiText = $json.text; // or $item(0).json.text

// Markdown 코드 블록에서 JSON 추출: ```json\n{...}\n```
const jsonMatch = geminiText.match(/```json\s*([\s\S]*?)\s*```/);

if (!jsonMatch) {
  throw new Error("JSON block not found in Gemini output.");
}

let parsed;
try {
  parsed = JSON.parse(jsonMatch[1]);
} catch (err) {
  throw new Error("Failed to parse JSON: " + err.message);
}

// 후처리 + 경로 가공
return {
  json: {
    ...$json,
    ...parsed,

    flpth_nm: Array.isArray(parsed.flpth_nm)
      ? parsed.flpth_nm.map(path => "https://www.kicox.or.kr" + path).join("@")
      : parsed.flpth_nm ? "https://www.kicox.or.kr" + parsed.flpth_nm : null,

    print_flpth_nm: Array.isArray(parsed.flpth_nm)
      ? "https://www.kicox.or.kr" + parsed.flpth_nm[0]
      : parsed.flpth_nm ? "https://www.kicox.or.kr" + parsed.flpth_nm : null,

    exc_instt_nm: parsed.exc_instt_nm || "test",
